<script>
import Cain from '@cain/'
export default {
  data () {
    return {
      pdfUrl: 'http://t.cn/RDUAhrn',
      overlay: true,
      baseInfo: {},
      certificateList: [],
      otherWork: [],
      skillList: [],
      starList: [],
      workList: [],
      isShowContact: false,
      dialogStatus: false
    }
  },
  async created () {
    wx.showLoading({
      title: '加载中',
      mask: true
    })
  },
  async mounted () {
    this.getData()
  },
  methods: {
    // 获取数据
    async getData () {
      // 本地方式
      // this.setData(LocalData)
      // 远程方式
      // const self = this
      const res = await Cain.post('haze.liu.json')
      this.baseInfo = res.baseInfo
      for (let item of res.workList) {
        item.isShow = false
      }
      for (let item of res.otherWork) {
        item.isShow = false
      }
      this.workList = res.workList
      this.otherWork = res.otherWork
      this.skillList = res.skillList
      this.starList = res.starList
      this.certificateList = res.certificateList
      this.isShowContact = res.isShowContact
      this.overlay = false
      wx.hideLoading()
      wx.showShareMenu({
        withShareTicket: true
      })
    },
    toggleAccordionItem (item) {
      item.isShow = !item.isShow
    },
    // 设置剪切板内容
    setClipboard (val) {
      Cain.setClipboard(val)
    },
    // 打电话
    callPhone (val) {
      Cain.callPhone(this.baseInfo.mobilePhone)
    },
    showDialog () {
      this.dialogStatus = true
    },
    closeDialog () {
      this.dialogStatus = false
    },
    // 打开赞赏码
    openZSCode () {
      let url = 'https://www.tenbamboo.com/common/image/code.jpg'
      wx.previewImage({
        current: url,
        urls: [url]
      })
    }
  }
}
</script>

